using LuegoPago.Logic.Core; using Microsoft.AspNetCore.Mvc; using static LuegoPago.Logic.Core.Utility;  namespace LuegoPago.Controllers {     [ApiController]     [Route("[controller]")]     public class LuegoPagoController : Controller     {         readonly Utility utility;         public LuegoPagoController()         {             utility = new Utility();         }          [HttpGet]         public async Task<IActionResult> Index(string dayWeek, int duration = 30)         {             dayWeek = dayWeek.Trim().ToLower();              try             {                 if (!Enum.TryParse(typeof(DayWeek), dayWeek, out object dayWeekFind))
                    return BadRequest("Día no encontrado");                   double space = await utility.AvailableTimeAsync(dayWeek, duration);                  string reponse = $"Para el día {dayWeek} queda(quedan) {space} espacio(s) de {duration} min";                 return Ok(reponse);             }             catch (Exception ex)             {                 return BadRequest("Se presenetó un problema");             }                      }     } } 